INSERT INTO `certificate_types` (`id`, `type_name`, `description`, `created_at`, `updated_at`) VALUES (1, '英语专业证书', '\r\n英语专业证书是英语学习者在语言能力、语言知识、语言技能以及跨文化交际能力等方面达到一定专业水平的重要凭证。它由权威教育机构或专业认证机构颁发，通常基于严格、系统的考核体系，涵盖听力、口语、阅读、写作和翻译等多个维度。', '2025-05-18 23:16:39', '2025-05-18 23:45:40');
INSERT INTO `certificate_types` (`id`, `type_name`, `description`, `created_at`, `updated_at`) VALUES (2, '计算机专业证书', '计算机专业证书是计算机领域专业技能与知识水平的重要认证，由权威教育机构、行业协会或专业认证组织颁发。该证书以科学严谨的考核体系为基础，全面评估持证人在计算机科学与技术、软件开发、信息技术、网络安全、数据分析等专业领域的理论知识、实践能力以及问题解决能力。证书内容包含持证人基本信息、考核科目、成绩评定、证书编号及有效期限等关键信息，确保其唯一性与可追溯性。', '2025-05-18 23:47:22', '2025-05-18 23:47:22');
